css:
.top-bar {
    font-size: 95%;
    background-color: #d690a9;  
}

.top-bar__nav {
    max-width: 80rem;
    color: #ffffff;
    margin-left: auto;
    margin-right: auto;
    padding: var(--spacer-2) var(--spacer-2) var(--spacer-1);
}

.header__nav a {
    color: #817f88;
    padding-right: 1rem;
}

@media (min-width: 992px)
.col-lg-auto {
    flex: 0 0 auto;
    width: auto;
    max-width: 100%;
}

@media (min-width: 992px)
.col-lg-3 {
    flex: 0 0 25%;
    max-width: 25%;
}

@media (min-width: 992px)
.col-lg {
    flex-basis: 0;
    flex-grow: 1;
    max-width: 25%;

}

.header__nav {
    text-align: initial;
    font-size: var(--font-size-base); 
    line-height: var(--line-height-bas);
    line-height: var(--line-height-lg);
    margin-top: 30px;
    margin-bottom: 10px;
}

.footer {
      color: #ffffff;
    font-size: var(--font-size-lg)
   background: #3b9475;
}

.footer__store .footer__credits, .footer__title, .footer a, .footer i
    color: #ffffff;
}

